package String_Program;

public class RightAngleTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i,j;
		int sum=0;
		//int n=5;//no of rows to print
		
		for(i=1;i<=5;i++){
			//System.out.println();
			for(j=1;j<=i;j++){ 
				if(i==1)
				{System.out.print(i);
				//break;
	//			System.out.println();
			}
				else {
					sum=i+(sum-1);
					
			}
			}
			System.out.println();
			
	}
	}

}
